mapper.select:
SELECT challenge.challenge_id, challenge_name, thumbnail, startdate, enddate, createdate, challenge.member_id, subcategory_id, challenge_type, count, sub_category_name from challenge, sub_category, (SELECT challenge.challenge_id, COUNT(*) as count from challenge, challenge_join WHERE challenge.challenge_id = challenge_join.challenge_id GROUP BY challenge.challenge_id) count_table WHERE challenge.subcategory_id = sub_category.sub_category_id AND challenge.challenge_id = count_table.challenge_id
AND challenge_name like CONCAT('%', #{searchWord}, '%')
AND challenge_type = "무료" AND challenge_type = "유료"
AND startdate > NOW()
AND createdate < #{createdate} GROUP BY challenge.challenge_id order by createdate DESC LIMIT 12;
AND createdate > #{createdate} GROUP BY challenge.challenge_id order by createdate ASC LIMIT 12;
AND if(#{count} = '1', count < 2 and createdate < #{createdate}, count < #{count} and createdate < #{createdate}) GROUP BY challenge.challenge_id order by count desc, createdate DESC LIMIT 12;

SELECT challenge.challenge_id, challenge_name, thumbnail, startdate, enddate, 
		createdate, challenge.member_id, subcategory_id, challenge_type, count, sub_category_name
from challenge, sub_category, (SELECT challenge.challenge_id, COUNT(*) as count  
								from challenge, challenge_join
								WHERE challenge.challenge_id = challenge_join.challenge_id
								GROUP BY challenge.challenge_id) count_table
WHERE challenge.subcategory_id = sub_category.sub_category_id AND challenge.challenge_id = count_table.challenge_id AND subcategory_id = #{subcategory_id}
	 AND challenge_name like CONCAT('%', #{searchWord}, '%')

		AND challenge_type = "무료"
		AND challenge_type = "유료"

	AND startdate > NOW()




			 AND createdate < #{createdate} 
			GROUP BY challenge.challenge_id order by createdate DESC LIMIT 12;



			 AND createdate > #{createdate}
			GROUP BY challenge.challenge_id order by createdate ASC LIMIT 12;



			 AND if(#{count} = '1', count < 2 and createdate < #{createdate}, count < #{count} and createdate < #{createdate})
			GROUP BY challenge.challenge_id order by count desc, createdate DESC LIMIT 12;

SELECT challenge.challenge_id, challenge_name, thumbnail, startdate, enddate, createdate, challenge.member_id, subcategory_id, challenge_type, count, sub_category_name from challenge, sub_category, main_category, (SELECT challenge.challenge_id, COUNT(*) as count from challenge, challenge_join WHERE challenge.challenge_id = challenge_join.challenge_id GROUP BY challenge.challenge_id) count_table WHERE challenge.subcategory_id = sub_category.sub_category_id AND challenge.challenge_id = count_table.challenge_id AND sub_category.main_category_id = main_category.main_category_id
AND main_category_name = '취미' AND main_category_name = '자기계발'
AND challenge_name like CONCAT('%', #{searchWord}, '%')
AND challenge_type = "무료" AND challenge_type = "유료"
AND startdate > NOW()
AND createdate < #{createdate} GROUP BY challenge.challenge_id order by createdate DESC LIMIT 12;
AND createdate > #{createdate} GROUP BY challenge.challenge_id order by createdate ASC LIMIT 12;
AND if(#{count} = '1', count < 2 and createdate < #{createdate}, count < #{count} and createdate < #{createdate}) GROUP BY challenge.challenge_id order by count desc, createdate DESC LIMIT 12;

mapper.delete:
delete from
challenge_join where challenge_id=#{challenge_id} and
member_id=#{member_id}
